"""Class for represent series in python"""import sympy as spclass Series:    def __init__(self, expr, var, limits = (1, float("inf"))):        self.limits = limits        self.expr = expr        self.var = sp.Symbol(var)    def get_expr(self):        return self.expr    def get_var(self):        return  self.var    def get_limits(self):        return self.limits    def get_str_expr(self):        return str(self.expr)    def get_str_var(self):        return str(self.var)    def set_expr(self, expr):        self.expr = expr    def __str__(self):        #sp.pretty_print(self.expr)        return self.get_str_expr()