"""Модуль реализации класса Series"""import sympy as spclass Series:    """    Класс для реализации численного ряда    """    def __init__(self, expr, var, limits = (1, float("inf"))):        """        Метод инициализации численного ряда        Args:            expr (sympy.Expr): Общий член ряда            var (str): переменная индексации            limits (tuple): пределы суммирования        """        self.limits = limits        self.expr = expr        self.var = sp.Symbol(var)    def get_expr(self):        """        Метод получения общего члена ряда        Returns:            sympy.Expr: Общий член ряда        """        return self.expr    def get_var(self):        """        Метод получения переменной индексации        Returns:            sympy.Symbol:  переменная индексации        """        return  self.var    def get_limits(self):        """        Метод получения пределов суммирования        Returns:            tuple: Пределы суммирования        """        return self.limits    def get_str_expr(self):        """        Метод получения строкового представления общего члена численного ряда        Returns:            str: Cтроковое представление общего члена численного ряда        """        return str(self.expr)    def get_str_var(self):        """        Метод получения строкового представления переменной индексации        Returns:            str: Переменная индексации        """        return str(self.var)    def set_expr(self, expr):        """        Метод изменения общего члена численного ряда        Args:            expr (sp.Expr): Новый общий член численного ряда        """        self.expr = expr    def __str__(self):        """        Метод строкового представления объекта класса Series        Returns:            str: Строковое представление объекта класса Series        """        return self.get_str_expr()