"""Модуль с реализацией стека"""from collections import dequeclass Stack:    """    Класс для реализации стека    """    def __init__(self):        """        Метод инициализации стека        """        self.items = deque()    def is_empty(self):        """        Метод проверки является ли стек пустым        Returns:            bool: True - пустой, False - непустой        """        if not self.items:            return True        else:            return False    def push(self, item):        """        Метод пополнения стека        Args:            item: добавляемый элемент        Returns:            None:        """        self.items.append(item)    def pop(self):        """        Метод удаления элемента из стека, если он непустой        """        if not self.is_empty():            return self.items.pop()        else:            return 'stack is empty'    def peek(self):        """        Метод просмотра крайнего элемента стека, елси он непустой        Returns:        """        if not self.is_empty():            return self.items[-1]        else:            return 'stack is empty'